import React, { useState, useCallback } from 'react';
import ReactFlow, { 
  addEdge, 
  Background, 
  Controls,
  useNodesState,
  useEdgesState,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { message } from 'antd';
import { nodeTypes } from './components/Nodes';
import Toolbar from './components/Toolbar';
import ConfigPanel from './components/ConfigPanel';
import WorkflowControls from './components/WorkflowControls';
import { executeWorkflow } from './services/api';
import './styles/index.css';

const App = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);

  const onConnect = useCallback((params) => {
    setEdges((eds) => addEdge(params, eds));
  }, [setEdges]);

  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();

      const type = event.dataTransfer.getData('application/reactflow');
      if (!type) return;

      const position = {
        x: event.clientX - 200,
        y: event.clientY - 40,
      };

      const newNode = {
        id: `${type}-${Date.now()}`,
        type,
        position,
        data: { label: `${type} node` },
      };

      setNodes((nds) => nds.concat(newNode));
    },
    [setNodes]
  );

  const onNodeClick = useCallback((_, node) => {
    setSelectedNode(node);
  }, []);

  const onConfigChange = useCallback((config) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === selectedNode.id) {
          node.data = { ...node.data, ...config };
        }
        return node;
      })
    );
  }, [selectedNode, setNodes]);

  const handleStart = async () => {
    try {
        console.log('开始执行工作流');
        console.log('节点配置:', nodes);
        console.log('连线:', edges);
        
        const workflow = { nodes, edges };
        const result = await executeWorkflow(workflow);
        
        console.log('执行结果:', result);
        message.success(`工作流执行成功: ${result.data?.message || '文档已生成'}`);
    } catch (error) {
        console.error('执行错误:', error);
        console.error('错误详情:', error.response?.data);
        message.error(`执行失败: ${error.response?.data?.message || error.message}`);
    }
};

  return (
    <div className="app-container">
      <Toolbar />
      <div className="workflow-area">
        <WorkflowControls
          onStart={handleStart}
          onSave={() => {}}
          onLoad={() => {}}
        />
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onDrop={onDrop}
          onDragOver={onDragOver}
          onNodeClick={onNodeClick}
          nodeTypes={nodeTypes}
        >
          <Background />
          <Controls />
        </ReactFlow>
      </div>
      <ConfigPanel
        selectedNode={selectedNode}
        onConfigChange={onConfigChange}
      />
    </div>
  );
};

export default App;
